mapper.update:
UPDATE POST
SET POSTREADCOUNT = POSTREADCOUNT + 1
WHERE POSTKEY = #{POSTKEY}

mapper.select:
SELECT COUNT(*)
FROM GCOMMENT
WHERE POSTKEY = #{postkey} AND GROUPKEY = #{groupkey}


SELECT * FROM (	SELECT ROWNUM R, P.*
				FROM (	SELECT POSTKEY, GROUPKEY, POSTTITLE, POSTREADCOUNT, TO_CHAR(POSTDATE, 'YYYY-MM-DD') AS POSTDATE
						FROM POST
						WHERE USERKEY = #{userkey} AND GROUPKEY = #{groupkey}
						ORDER BY POSTDATE DESC) P
			  )
WHERE R >= #{START} AND R <= #{END}

SELECT * FROM ( SELECT ROWNUM R, C.* FROM ( SELECT USERKEY, POSTKEY, COMMENTCONTENT, GROUPKEY, REPLYCOUNT, POSTTITLE, TO_CHAR(COMMENTDATE, 'YYYY-MM-DD') COMMENTDATE, POSTREADCOUNT, COMMENTSHOW FROM GCOMMENT LEFT JOIN (SELECT POSTKEY, POSTTITLE, REPLYCOUNT, POSTREADCOUNT FROM POST LEFT JOIN (SELECT POSTKEY, COUNT(POSTKEY) REPLYCOUNT FROM GCOMMENT GROUP BY POSTKEY) USING (POSTKEY)) USING(POSTKEY) WHERE USERKEY = #{userkey} AND GROUPKEY = #{groupkey}
AND COMMENTSHOW <> 1
ORDER BY COMMENTDATE DESC) C ) WHERE R >= #{START} AND R <= #{END}

SELECT COUNT(*)
FROM POSTLIKE
WHERE USERKEY = #{userkey} AND GROUPKEY = #{groupkey} AND POSTKEY = #{postkey}

mapper.delete:
DELETE 
FROM POSTLIKE
WHERE USERKEY = #{userkey} AND GROUPKEY = #{groupkey} AND POSTKEY = #{postkey}

mapper.select:
SELECT COUNT(POSTKEY)
FROM POSTLIKE
WHERE GROUPKEY = #{groupkey} AND POSTKEY = #{postkey}
